customCss:
.event {
  border: 1px solid #4CAF50;
  padding: 10px;
  margin: 10px 0;
  background-color: #f9f9f9;
}
.event-name {
  font-size: 20px;
  font-weight: bold;
  color: #333;
}
.event-countdown {
  font-size: 16px;
  color: #f44336;
  margin-top: 10px;
}
.event-name:hover {
  color: #0d47a1;
}
.countdown {
  color: #ff9800;
  font-weight: bold;
}
.live-now {
  color: #4caf50;
  font-weight: bold;
  animation: blink 1s infinite;
}
.match-end {
  color: #9e9e9e;
  font-weight: bold;
}
@keyframes blink {
  50% {
    opacity: 0;
  }
}

customJs:
function updateStatus() {
    const statusElements = document.querySelectorAll('[id^="status"]');
    const currentTime = new Date().getTime();

    statusElements.forEach(element => {
        const startTime = new Date(element.getAttribute('data-start')).getTime();
        const durationHours = parseFloat(element.getAttribute('data-duration'));
        const endTime = startTime + durationHours * 60 * 60 * 1000;

        if (currentTime < startTime) {
            const timeDiff = startTime - currentTime;
            const hours = Math.floor(timeDiff / (1000 * 60 * 60));
            const minutes = Math.floor((timeDiff % (1000 * 60 * 60)) / (1000 * 60));
            const seconds = Math.floor((timeDiff % (1000 * 60)) / 1000);

            element.innerHTML = `<span>${hours}h</span> <span>${minutes}m</span> <span>${seconds}s</span> `;
            element.className = 'countdown';
        } else if (currentTime >= startTime && currentTime <= endTime) {
            element.innerHTML = `<span> Watch Now </span>`;
            element.className = 'live-now blink';
        } else {
            element.innerHTML = `<span> Match End </span>`;
            element.className = 'match-end';
        }
    });
}

// Check every second if the current time falls within the start and end time
setInterval(updateStatus, 1000);

// Initial check when the page loads
updateStatus();

document.addEventListener('DOMContentLoaded', function() {
    // Example: Highlight table rows on hover
    var rows = document.querySelectorAll('table tr');
    rows.forEach(function(row) {
        row.addEventListener('mouseover', function() {
            this.style.backgroundColor = '#f2f2f2';
        });
        row.addEventListener('mouseout', function() {
            this.style.backgroundColor = '';
        });
    });
});
